body.onMount:
try{
Session.set("instance_change", false);

Session.set("instance_loading", true);
Session.set("judge", null);
Session.set("next_step_id", null);
Session.set("next_step_multiple", null);
Session.set("next_user_multiple", null);
Session.set("box", props.data.side_listview_id);


 window.instanceDetailBlazeRenderView = Blaze.render(Blaze._getTemplate('instance_view_wrapper'), document.getElementsByClassName('instance-detail-service-wrapper')[0])}catch(error){console.error(error)}; window.instanceDetailBlazeRenderView?.autorun(()=>{Session.set("instanceId", Session.get("_record_id_instanceId"))});

api.requestAdaptor:
const { recordId } = api.body;
api.data = {
  query: `{instance_task:instance_tasks__findOne(id: "${recordId}"){_id,instance}}`
};

return api;

api.adaptor:
let boxName = api.body.side_listview_id === 'inbox' ? 'inbox' : 'outbox';
payload.data = {
  instanceId: payload.data.instance_task.instance,
  boxName: boxName
}; setTimeout(()=>{window.Session.set('_record_id_instanceId', payload.data.instanceId)}, 300); 
return payload;
