body.onMount:
Session.set("instance_change", false);

Session.set("instance_loading", true);
Session.set("judge", null);
Session.set("next_step_id", null);
Session.set("next_step_multiple", null);
Session.set("next_user_multiple", null);
Session.set("instanceId", props.data.instanceId);
Session.set("box", props.data.boxName);


 window.instanceDetailBlazeRenderView = Blaze.render(Blaze._getTemplate('instance_view_wrapper'), document.getElementsByClassName('instance-detail-service-wrapper')[0])

api.requestAdaptor:
const { recordId } = api.body;
api.data = {
  query: `{instance_task:instance_tasks__findOne(id: "${recordId}"){_id,instance}}`
};

return api;

api.adaptor:
let boxName = api.body.side_listview_id === 'inbox' ? 'inbox' : 'outbox';
payload.data = {
  instanceId: payload.data.instance_task.instance,
  boxName: boxName
}
return payload;
