actions.script:
//整理分组数据
let setting_groups = event.data.setting_groups;
if (!_.find(setting_groups, { is_default: true })) {
  setting_groups.unshift({
    "id": "未分组",
    "group_name": "未分组",
    "visible_on": "",
    "is_default": true
  })
}
setting_groups.forEach(function (group) {
  if (group.is_default) {
    group.group_name = "未分组";
    group.visible_on = "";
  }
  if (!group.id) {
    group.id = group.group_name;
  }
})

//整理字段与分组关系的数据
const fieldForGroup = {};
let oldGroup = _.cloneDeep(event.data.fieldForGroup);
setting_groups.forEach(function (group) {
  if (_.has(oldGroup, group.id)) {
    fieldForGroup[group.id] = oldGroup[group.id];
    oldGroup = _.omit(oldGroup, group.id);
  } else {
    fieldForGroup[group.id] = [];
  }
})
if (oldGroup && !_.isEmpty(oldGroup)) {
  _.forEach(oldGroup, function (value, key) {
    fieldForGroup["未分组"] = _.unionBy(fieldForGroup["未分组"], value);
  });
}
console.log("setting_groups,fieldForGroup===>", setting_groups, fieldForGroup);

doAction({
  actionType: 'setValue',
  componentId: 'service_field_design',
  args: {
    value: {
      groups: setting_groups,
      fieldForGroup
    }
  }
});

const newGroups = _.keys(event.data.value);
const lastGroups = _.map(context.props.data.groups, 'group_name');
if (newGroups != lastGroups) {
  doAction({
    actionType: 'setValue',
    componentId: 'service_field_design',
    args: {
      value: {
        fieldForGroup: event.data.value, groups: field_groups = _.sortBy(context.props.data.groups, function (group) { return _.findIndex(newGroups, function (group_name) { return group.group_name === group_name; }); })
      }
    }
  });
}

api.requestAdaptor:
var graphqlOrder = "";
//根据groups，修改对象的字段分组field_groups
var field_groups = _.cloneDeep(api.data.groups);
_.remove(field_groups, { is_default: true });
field_groups = field_groups.map(function (group) {
  return _.omit(group, 'id');
})
const groupOrder = 'upsert0:objects__upsert(id:"' + api.data.$self.designObjectId + '" , doc:' + JSON.stringify(JSON.stringify({ field_groups })) + ') {_id}';
graphqlOrder += groupOrder;

//根据design_field,修改对象字段的sort_no与groups
var index = 1;
_.forEach(api.data.design_field, function (items, key) {
  _.forEach(items, function (item) {
    const field = _.find(api.data.fields, { 'id': item });
    const itemOrder = 'upsert' + index + ':object_fields__upsert(id:"' + item + '" , doc:' + JSON.stringify(JSON.stringify({ object: api.data.$self.designObjectName, _name: field._name, label: field.label, sort_no: index * 10, group: key == "未分组" ? null : key })) + '){_id}\n';
    graphqlOrder += itemOrder;
    index++;
  })
})

graphqlOrder = 'mutation {' + graphqlOrder + '}';
return {
  ...api,
  data: {
    query: graphqlOrder
  }
}

api.adaptor:
if (payload.errors) {
  payload.status = 2;
  payload.msg = window.t ? window.t(payload.errors[
    0
  ].message) : payload.errors[
    0
  ].message;
}
return payload;

//筛选出可编辑的字段

var oldFields = _.filter(payload.data.fields, function (obj) { return obj.recordPermissions.allowEdit; });
//将group为空的字段分为未分组的组中
oldFields = _.map(oldFields, function (obj) {
  obj.originId = obj._id;
  if (obj.group === null) {
    obj.group = "未分组";
  }
  if (obj.is_system) {
    obj._id = api.data.designObjectName + "." + obj._name;
  }
  return obj;
});

//整合出字段的属性集合
const fields = oldFields.map((field) => {
  return {
    "id": field._id,
    "_name": field._name,
    "label": field.label,
    "_id": field.originId,
    "columnSpan": field.is_wide ? 2 : 1
  }
})

//从字段的group属性中，整合出字段分组的属性集合
let field_groups = _.map(_.uniqBy(oldFields, "group"), function (obj) {
  return {
    "id": obj.group,
    "group_name": obj.group,
    "visible_on": ""
  };
});

//合并对象上的field_groups与字段上的group
const groups = _.unionBy(api.data.field_groups, field_groups, 'id');

//判断是否第一次调用接口，不是的话（说明是触发了datachange事件），返回fields与fieldUpdateData
if (api.data.dataInitialed) {
  const eventData = api.data.eventData;
  let fieldUpdateData = "";
  const fieldForGroup = api.data.fieldForGroup;
  let fieldForGroupLength = 0;
  _.forIn(fieldForGroup, function (value, key) {
    fieldForGroupLength += value.length;
  });
  if (fieldForGroupLength < fields.length) {
    fieldUpdateData = {
      type: "insert",
      id: eventData.result.data.recordId
    }
  } else if (fieldForGroupLength > fields.length) {
    fieldUpdateData = {
      type: "delete",
      id: eventData._id
    }
  }
  return payload = {
    data: {
      fields,
      fieldUpdateData
    }
  }
}

//整合字段与分组的关系
const oldFieldsInGroups = _.groupBy(oldFields, "group");
const fieldForGroup = _.mapValues(_.groupBy(groups, "id"), function (group,key) {
  if (oldFieldsInGroups[key]) {
    return _.map(oldFieldsInGroups[key], function (obj) {
      return obj._id;
    });
  } else {
    return [];
  }
});

return payload = {
  data: {
    fields,
    groups,
    fieldForGroup,
    dataInitialed: true
  }
};

actions.script:
const fieldForGroup = context.props.data.fieldForGroup;
const fieldUpdateData = event.data.fieldUpdateData;
if (fieldUpdateData && fieldUpdateData.type == "delete") {
  _.forIn(fieldForGroup, function (group, group_name) {
    _.remove(group, function (field) {
      return field === fieldUpdateData.id;
    })
  });
} else if (fieldUpdateData && fieldUpdateData.type == "insert") {
  fieldForGroup["未分组"].push(fieldUpdateData.id);
}
