/**
 * 转为es5 支持的多行文本脚本
 * 传入function，且函数休开始结束为多行注释符号包裹住（中间需要回车符）
 * 注意传入的function函数体内不能包含字符/$
*/
var getMultiLineScript = function (fun) {
  var strFun = fun.toString();
  var lines = strFun.split(new RegExp("\\n"));
  lines.shift();
  lines.pop();
  return lines.join("");
}


